package com.kamiltest.demo.doa.model;import org.springframework.format.annotation.DateTimeFormat;import org.springframework.lang.Nullable;import javax.persistence.*;import javax.validation.constraints.NotNull;import javax.validation.constraints.Size;import java.util.Date;import java.util.Set;//add entity and check connection with database@Entitypublic class Worker {    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Id    private Long id;    @NotNull()    @Size(min = 2,max = 255,message = "Required length is between 2 and 255 characters")    private String name;    @NotNull()    @Size(min = 2,max = 255,message = "Required length is between 2 and 255 characters")    private String surname;    @NotNull()    @Size(min = 2,max = 255,message = "Required length is between 2 and 255 characters")    private String City;    @NotNull()    @Size(min = 2,max = 255,message = "Required length is between 2 and 255 characters")    private String Street;    @Nullable    @Temporal(TemporalType.DATE)    @DateTimeFormat(pattern = "yyyy-MM-dd")    private Date utilCalendar;//    e.setUtilCalendar(new GregorianCalendar(2019, 6, 18));    @ManyToMany    @JoinTable(name = "workers_orders",    joinColumns = @JoinColumn(name = "worker_id"),    inverseJoinColumns = @JoinColumn(name = "order_id"))    @Nullable    private Set<Order> ordersToDo;    public Worker() {    }    public Worker(Long id, String name, String surname, String city, String street, Date utilCalendar, Set<Order> ordersToDo) {        this.id = id;        this.name = name;        this.surname = surname;        City = city;        Street = street;        this.utilCalendar = utilCalendar;        this.ordersToDo = ordersToDo;    }    public Long getId() {        return id;    }    public void setId(Long id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public String getSurname() {        return surname;    }    public void setSurname(String surname) {        this.surname = surname;    }    public String getCity() {        return City;    }    public void setCity(String city) {        City = city;    }    public String getStreet() {        return Street;    }    public void setStreet(String street) {        Street = street;    }//    @Nullable//    public Date getUtilCalendar() {//        return utilCalendar;//    }////    public void setUtilCalendar(@Nullable Date utilCalendar) {//        this.utilCalendar = utilCalendar;//    }//    public Date getUtilCalendar()//    {//        return new GregorianCalendar(this.utilCalendar.get(Calendar.YEAR),//                this.utilCalendar.get(Calendar.MONTH),//                this.utilCalendar.get(Calendar.DAY_OF_MONTH)).getTime();//    }////    public String formatUtilCalendar() {//        String date = this.utilCalendar.get(Calendar.YEAR) + "-" +//                this.utilCalendar.get(Calendar.MONTH) + "-" +//                this.utilCalendar.get(Calendar.DAY_OF_MONTH) ;//        return  date;//    }//    @Nullable//    public Calendar getUtilCalendar() {//        return utilCalendar;//    }////    public void setUtilCalendar(Calendar utilCalendar) {//        this.utilCalendar = utilCalendar;//    }    @Nullable    public Date getUtilCalendar() {        return utilCalendar;    }    public void setUtilCalendar(@Nullable Date utilCalendar) {        this.utilCalendar = utilCalendar;    }    public Set<Order> getOrdersToDo() {        return ordersToDo;    }    public void setOrdersToDo(Set<Order> ordersToDo) {        this.ordersToDo = ordersToDo;    }    public int countOrders()    {        return this.ordersToDo.size();    }    @Override    public String toString() {        return "Worker{" +                "id=" + id +                ", name='" + name + '\'' +                ", surname='" + surname + '\'' +                ", City='" + City + '\'' +                ", Street='" + Street + '\'' +                ", utilCalendar=" + utilCalendar +                ", ordersToDo=" + ordersToDo +                '}';    }}